def verify(data: list):
    if len(data) == 0:
        return False
    for number in range(0, 18):
        postion = 'P' + str(number)
        if data.count(postion) > 1:
            return False
    if data[-1][0] == 'P':
        return False
    return True

def split(data: list):
    return_tuple = [None] * 18
    for i, item in enumerate(data):
        if item[0] == 'P':
            index = int(item[1:])
            idxes = []
            for j in range(i + 1, len(data)):
                if data[j][0] == 'P':
                    break
                idxes.append(data[j])

            return_tuple[index] = idxes
    return return_tuple

# verification
def raw_to_tuple(raw_data: list):

    if verify(raw_data):
        return split(raw_data)
    else:
        return False

