def list_to_tuple(batch_list: list[str]) -> list[list[list]]:
    return translate(decode(batch_list))


def decode(batch_list: list[str]) -> list[list[list]]:
    final_results = []
    for sample in batch_list:
        tuple_18s = list(sample.split())
        start_id = tuple_18s.index('[CLS]')
        if tuple_18s.count('[PAD]'):
            end_id = tuple_18s.index('[PAD]')
        else:
            end_id = tuple_18s.index('[SEP]', 1)
        if end_id == -1:
            continue
        tuple_18s = tuple_18s[start_id + 1: end_id]
        separate_idxes = [idx for idx, char in enumerate(tuple_18s) if char == 'SPILT']
        separate_lists = []
        if len(separate_idxes) >= 2:
            for i in range(len(separate_idxes) - 1):
                cur_idx, next_idx = separate_idxes[i], separate_idxes[i + 1]
                separate_lists.append(tuple_18s[cur_idx + 1: next_idx])
            separate_lists.append(tuple_18s[:separate_idxes[0]])
            separate_lists.append(tuple_18s[separate_idxes[-1] + 1:])
        elif len(separate_idxes) == 1:
            separate_lists.append(tuple_18s[: separate_idxes[0]])
            separate_lists.append(tuple_18s[separate_idxes[0] + 1:])
        else:
            separate_lists.append(tuple_18s[:])
        final_results.append(separate_lists)
    return final_results


def verify(data: list):
    if len(data) == 0:
        return False
    for number in range(0, 18):
        postion = 'P' + str(number)
        if data.count(postion) > 1:
            return False
    if data[-1][0] == 'P':
        return False
    return True


def split(data: list):
    return_tuple = [None for _ in range(18)]
    for i, item in enumerate(data):
        if item[0] == 'P':
            index = int(item[1:])
            idxes = []
            for j in range(i + 1, len(data)):
                if data[j][0] == 'P':
                    break
                idxes.append(data[j])

            return_tuple[index] = idxes
    return return_tuple


def raw_to_tuple(raw_data: list):
    if verify(raw_data):
        return split(raw_data)
    else:
        return False


def translate(batch_list: list[list[list]]) -> list[list[list]]:
    batch_tuples = []
    for sample in batch_list:
        tuples = []
        for raw_list in sample:
            return_value = raw_to_tuple(raw_list)
            if return_value:
                tuples.append(return_value)
        batch_tuples.append(tuples)
    return batch_tuples

